CREATE TABLE insee_data (id BIGINT AUTO_INCREMENT, code_insee VARCHAR(255), access_proxi VARCHAR(255), access_inter VARCHAR(255), licence_sport VARCHAR(255), emploi VARCHAR(255), salaire VARCHAR(255), espace_nature VARCHAR(255), dist_travail VARCHAR(255), access_doctor VARCHAR(255), access_care VARCHAR(255), INDEX code_insee_idx (code_insee), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE postal_code (id BIGINT AUTO_INCREMENT, code_insee VARCHAR(255), code_postal VARCHAR(255), nom_commune VARCHAR(255), departement VARCHAR(255), region VARCHAR(255), superficie VARCHAR(255), population VARCHAR(255), geo_point_2d VARCHAR(255), geo_shape TEXT, code_departement VARCHAR(255), INDEX nom_commune_idx (nom_commune), INDEX code_postal_idx (code_postal), INDEX code_insee_idx (code_insee), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE securite_data (id BIGINT AUTO_INCREMENT, code_insee VARCHAR(255), commune VARCHAR(255), population VARCHAR(255), departement VARCHAR(255), zone VARCHAR(255), compagnies_de_gendarmerie_departementale VARCHAR(255), classement_violences_personnes VARCHAR(255), classement_atteintes_biens VARCHAR(255), classement_violences_physiques VARCHAR(255), classement_cambriolages VARCHAR(255), classement_vols_voitures VARCHAR(255), coordonnees_geographiques VARCHAR(255), INDEX code_insee_idx (code_insee), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
ALTER TABLE insee_data ADD CONSTRAINT insee_data_code_insee_postal_code_code_insee FOREIGN KEY (code_insee) REFERENCES postal_code(code_insee) ON DELETE CASCADE;
ALTER TABLE securite_data ADD CONSTRAINT securite_data_code_insee_postal_code_code_insee FOREIGN KEY (code_insee) REFERENCES postal_code(code_insee) ON DELETE CASCADE;
